mapper.select:
SELECT COUNT(*) 
FROM Member 
WHERE member_id = #{memberId}

SELECT COUNT(*) 
FROM Member 
WHERE nickname = #{nickname}

SELECT COUNT(*) 
FROM Member 
WHERE email = #{email}

SELECT COUNT(*) 
FROM Member 
WHERE phone = #{phone}

SELECT member_id 
FROM Member 
WHERE name = #{name} 
AND phone = #{phone}

mapper.update:
Update Member
SET name = #{name}, pass = #{pass}, zipcode = #{zipcode}, address = #{address}, address2 = #{address2}, email = #{email}, email_get = #{emailGet}, phone = #{phone}
WHERE member_no = #{memberNo}

mapper.delete:
DELETE FROM Member
WHERE member_type = 2
	AND withdrawal_end_date < NOW();

mapper.select:
SELECT COUNT(community_board_no)
FROM Community
WHERE member_no = #{memberNo}

SELECT COUNT(community_reply_no)
FROM CommunityReply 
WHERE member_no = #{memberNo}

SELECT COUNT(qna_board_no) 
FROM QnA
WHERE member_no = #{memberNo}

SELECT COUNT(qna_reply_no) 
FROM QnAReply
WHERE member_no = #{memberNo}

SELECT COUNT(review_no) 
FROM Review
WHERE member_no = #{memberNo}

mapper.update:
UPDATE member 
SET pass = #{newPassword} 
WHERE member_id = #{memberId}
