mapper.select:
SELECT *
FROM Review
WHERE pro_no = #{proNo}

SELECT COUNT(*)
FROM ProfessionalAdvancedInformation
WHERE pro_no = #{proNo}
  AND item_no = #{itemNo}

mapper.delete:
DELETE FROM Professional
WHERE pro_no = #{proNo}
AND NOT EXISTS (SELECT 1 FROM Portfolio WHERE pro_no = #{proNo})

mapper.select:
SELECT pro_no, pro_advanced_no
FROM Portfolio
WHERE portfolio_no = #{portfolioNo}

mapper.update:
UPDATE Portfolio
SET visibility = #{status}
WHERE portfolio_no = #{portfolioNo}
